import React,{useState} from 'react'



import Video from '../../videos/video.mp4';
import { HeroContainer , HeroBg, VideoBg , HeroContent, HeroH1 , HeroP, HeroBtnWraper,a} from './HeroElements';

const HeroSection = () => {
    const [hover , setHover] = useState(false);

    const onHover = () => setHover(!hover);

    return (
        <HeroContainer id = "home">
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video} type='video/mp4'  />
            </HeroBg>
            <HeroContent>
                <HeroH1>
                    Virtual Banking Made Easy
                </HeroH1>
                <HeroP>
                    Sign up for a new account today and receive $100 in credits towards your next payment.
                </HeroP>
                <HeroBtnWraper>
                    <Button to="signup" onMouseEnter={onHover} onMouseLeave={onHover} > 
                        Get Started {hover ? <ArrowForward /> : <ArrowRight></ArrowRight>}
                    </Button>
                </HeroBtnWraper>
            </HeroContent>

        </HeroContainer>
    )
}

export default HeroSection
